package com.syntax.class09;

public class Task {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=5;i>=1;i--) {
			for(int k=1;k<=i;k++) {
				System.out.print(i);
			}
			System.out.println();
		}
		
		System.out.println("===============================");
		
		
		for(int a=1;a<=5;a++) {
			for (int b=1; b<=a;b++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int a=4;a>=1;a--) {
			for (int b=1; b<=a;b++) {
				System.out.print("*");
			}
			System.out.println();
		
		
	}

}
}